/* -- DECLARE_PCB -- */
let board = new PCB();

let interior = geo.translate(geo.rectangle(width, height), [x+width/2, y+height/2]);


/* -- ADD_COMPONENTS -- */
let IC1 = board.add(FT230XS, {translate: pt(x+width/2.0, y+.7), name: 'IC1\nFT230XS'});
