// constants
const width = 1.45 // board width
const height = 1.2 // board height
const x = 1 // x origin
const y = 1 // y origin
const zt = 0 // top z
const zb = -0.06 // bottom z
const w = .015 // wire width
const mask = .004 // solder mask size
const border = 0.05 // rendering border


/* -- DECLARE_PCB -- */
let board = new PCB();

let interior = geo.translate(geo.rectangle(width, height), [x+width/2, y+height/2]);


/* -- ADD_COMPONENTS -- */
let J1 = board.add(header_FTDI, {translate: pt(x+width-.22, y+.5), rotate: 0, name: 'J1 FTDI\n3.3V'});
let IC1 = board.add(regulator_SOT223, {translate: pt(J1.posX-.25, J1.posY), name: 'IC1\n3.3V'});
let C1 = board.add(C_1206, {translate: pt(IC1.posX, IC1.posY-.25), name: 'C1 10uF'});
let C2 = board.add(C_1206, {translate: pt(IC1.posX, IC1.posY+.25), name: 'C2 1uF'});
let IC2 = board.add(ESP32_WROOM, {translate: pt(x+.35, y+.69), rotate: 90, name: 'IC2\nESP32-WROOM'});
let S1 = board.add(slide_switch, {translate: pt(IC1.posX, IC1.padY("out")+.4), rotate: 180, name: 'prog run'});
let S2 = board.add(button_6mm, {translate: pt(IC2.posX, IC2.padY("EN")-.2), name: 'reset'});
let R1 = board.add(R_1206, {translate: pt(S2.padX("L2")+.15, S2.posY), rotate: 90, name: 'R1\n10k'});
let C3 = board.add(C_1206, {translate: pt(R1.posX+.12, R1.posY), rotate: 90, name: 'C3\n.1uF'});

board.addShape("interior", interior);


/* -- ADD_WIRES -- */
board.wire([IC1.pad("G"),
            pt(IC1.padX("G")-.05, IC1.padY("G")),
            pt(IC1.padX("G")-.05, J1.padY("GND")+.1),
            pt(J1.padX("GND"), J1.padY("GND")+.1),
            J1.pad("GND")], w);

board.wire([IC1.pad("I"),
            pt(IC1.padX("I"), J1.padY("VCC")),
            J1.pad("VCC")], w);

board.wire([IC1.pad("O"),
            IC1.pad("out")], w);

board.wire([C1.pad("1"),
            pt(IC1.padX("G"), C1.padY("1")),
            IC1.pad("G")], w);

board.wire([C1.pad("2"),
            pt(IC1.padX("I"), C1.padY("2")),
            IC1.pad("I")], w);

board.wire([C2.pad("1"),
            pt(IC1.padX("G")-.05, C2.padY("1"))], w);

board.wire([C2.pad("2"),
            pt(C2.padX("2"), IC1.padY("out")),
            IC1.pad("out")], w);

board.wire([IC2.pad("GND1"),
            pt(IC2.padX("GND1"), IC2.padY("GND2")),
            IC2.pad("GND2")], w);

board.wire([IC2.pad("GND1"),
            IC2.pad("GND3")], w);

board.wire([IC2.pad("GND2"),
            pt(IC1.padX("G")-.05, IC2.padY("GND2")),
            pt(IC1.padX("G")-.05, IC1.padY("G")),
            IC1.pad("G")], w);

board.wire([IC2.pad("RX0"),
            pt(IC2.padX("RX0"), IC2.padY("RX0")+.1),
            pt(J1.padX("Tx")+.1, IC2.padY("RX0")+.1),
            pt(J1.padX("Tx")+.1, J1.padY("Tx")),
            J1.pad("Tx")], w);

board.wire([IC2.pad("TX0"),
            pt(IC2.padX("TX0"), IC2.padY("RX0")+.14),
            pt(J1.padX("Rx")+.14, IC2.padY("RX0")+.14),
            pt(J1.padX("Rx")+.14, J1.padY("Rx")),
            J1.pad("Rx")], w);

board.wire([S1.pad("3"),
            pt(IC1.padX("G")-.05, S1.padY("3")),
            pt(IC1.padX("G")-.05, IC2.padY("IO0")),
            IC2.pad("IO0")], w);

board.wire([S1.pad("2"),
            pt(S1.posX, J1.padY("GND")+.1)], w);

board.wire([S2.pad("L1"),
            S2.pad("L2")], w);

board.wire([S2.pad("L1"),
            pt(IC2.padX("EN"), S2.padY("L1")),
            IC2.pad("EN")], w);

board.wire([S2.pad("R1"),
            S2.pad("R2")], w);

board.wire([S2.pad("R1"),
            pt(IC2.padX("GND1"), S2.padY("R1")),
            IC2.pad("GND1")], w);

board.wire([IC2.pad("3V3"),
            pt(IC2.padX("3V3"), S2.posY),
            pt(IC1.padX("O"), S2.posY),
            IC1.pad("O")], w);

board.wire([R1.pad("1"),
            pt(R1.posX, R1.posY)], w);

board.wire([R1.pad("2"),
            pt(R1.posX, S2.padY("L2")),
            S2.pad("L2")], w);

board.wire([C3.pad("1"),
            pt(C3.posX, C3.posY)], w);

board.wire([C3.pad("2"),
            pt(C1.padX("1"), C3.padY("2")),
            C1.pad("1")], w);


// rendering
renderPCB({
  pcb: board,
  layerColors: {
    "interior": "#002d00ff",
    "B.Cu": "#ff4c007f",
    "F.Cu": "#ff8c00cc",
    "drill": "#ff3300e5",
    "padLabels": "#ffff99e5",
    "componentLabels": "#00e5e5e5",
  },
  limits: {
    x: [x-border, x+width+border],
    y: [y-border, y+height+border]
  },
  mm_per_unit: 25.4
})
